DROP TABLE IF EXISTS admin;
CREATE TABLE `admin` (  `id` int(10) NOT NULL auto_increment,  `admin` varchar(20) NOT NULL,  `password` varchar(30) NOT NULL,  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS admin_replay;
CREATE TABLE `admin_replay` (  `id` int(5) NOT NULL auto_increment,  `admin` varchar(30) NOT NULL,  `replay_time` varchar(30) NOT NULL,  `replay_content` text NOT NULL,  `user_message_id` varchar(10) NOT NULL,  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS lm;
CREATE TABLE `lm` (  `id` int(5) NOT NULL auto_increment,  `lm1` varchar(20) NOT NULL,  `lm2` varchar(20) NOT NULL,  `lm3` varchar(20) NOT NULL,  `lmid` int(5) NOT NULL,  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=84 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS news;
CREATE TABLE `news` (  `id` int(5) NOT NULL auto_increment,  `lm1` varchar(20) NOT NULL,  `lm2` varchar(20) NOT NULL,  `lm3` varchar(20) NOT NULL,  `title` varchar(20) NOT NULL,  `content` text NOT NULL,  `time` varchar(20) NOT NULL,  `hit` int(20) NOT NULL,  `adduser` varchar(20) NOT NULL,  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=35 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS user_message;
CREATE TABLE `user_message` (  `id` int(5) NOT NULL auto_increment,  `name` varchar(30) NOT NULL,  `time` varchar(30) NOT NULL,  `content` text NOT NULL,  `photo` varchar(40) NOT NULL,  `secret` int(11) NOT NULL default '0',  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=75 DEFAULT CHARSET=utf8;
